var today = new Date();
var time = "";

// check hours
if (today.getHours() < 10){
    time += "0"+ today.getHours() + " : ";
}
else{
    time += today.getHours() + " : ";
}

// check minutes
if (today.getMinutes() < 10){
    time += "0"+ today.getMinutes() + " : ";
}
else{
    time += today.getMinutes() + " : ";
}

// check mseconds
if (today.getSeconds() < 10){
    time += "0"+ today.getSeconds();
}
else{
    time += today.getSeconds();
}

// check for am, pm
if (today.getHours() < 12){
    time += "  am";
}
else{
    time += "  pm";
}

return {payload : time};

